#include "watermark.h"


int32_t Watermark::Encode(const std::string &FilePathIn,
                          const std::string &FilePathOut,
                          const std::string &EncodedWord,
                          const uint8_t *WImageBits,
                          const uint32_t &WImageBitsX,
                          const uint32_t &WImageBitsY,
                          const int32_t &Q,
                          const std::string &DCT_Pass) {

    enum _ContainerType {none, bmp, jpg};
    enum _WatermarkMethod {DCT, DWT};
    short method = DWT;
    short type = jpg;

    WatermarkImage *WImageEncoding = nullptr;
    if (EncodedWord.length() > 0) {
        WImageEncoding = new WatermarkImage(WImageBitsX, WImageBitsY, EncodedWord);
    }
    else if (WImageBits != nullptr) {
        if (WImageBitsX * WImageBitsY == 0)
            return -2; // Не предоставлены размеры водяного знака

        bool* EncodingImageBits = new bool[WImageBitsX * WImageBitsY];
        memset(EncodingImageBits, false, WImageBitsX * WImageBitsY);
        memcpy(EncodingImageBits, WImageBits, WImageBitsX * WImageBitsY * sizeof(bool));
        WImageEncoding = new WatermarkImage(WImageBitsX, WImageBitsY, EncodingImageBits);
        delete[] EncodingImageBits;
    }
    else {
        if (WImageEncoding != nullptr)  delete WImageEncoding;
        return -1;  // Водяной знак не был предоставлен
    }
    //--------------------------------------------------------------------


    // взяли битмап из jpeg
    uint32_t    temp_size = FileIO::GetActualFileSize(FilePathIn);
    char*       JPGData = new char[temp_size];
    memset(JPGData, 0, temp_size*sizeof(char));
    uint32_t    x = 0, y = 0;
    FileIO::ReadJPEGFIle(FilePathIn, nullptr, JPGData);
    FileIO::GetResolution(FilePathIn, x, y);

    int error = 0;

    // внедрили ЦВЗ в битмап и записали jpeg
    if (JPGData) {
        switch (method) {
            case DCT:
                {error = WatermarkEmbedding::EncodeBitmapBenham(JPGData, WImageEncoding, DCT_Pass, x, y, Q, -Q, type);  break;}
            case DWT:
                {error = WatermarkEmbedding::EncodeBitmapSoheili(JPGData, WImageEncoding, x, y, Q, type);  break;}
            default: break;
        }
        if (error == 0) {
            if (JPGData != nullptr)         delete[] JPGData;
            if (WImageEncoding != nullptr)  delete WImageEncoding;
            return -4;
        }
        FileIO::WriteJPEGFIle(FilePathOut, JPGData, x, y); // качество 100 определяется qt
    }
    else {
        if (JPGData != nullptr)         delete[] JPGData;
        if (WImageEncoding != nullptr)  delete WImageEncoding;
        return -3;
    }


    memset(JPGData, 0, FileIO::GetActualFileSize(FilePathOut));
    /*
    // прочитали jpeg и сверили ЦВЗ
    FileIO::ReadJPEGFIle(file_edited, nullptr, JPGData);
    if (JPGData) {
        std::string DecodedWord;
        switch (method) {
        case DCT: {
            WImageRecovered = WatermarkEmbedding::DecodeBitmapBenham(JPGData, WImageDecoding, PassWord, x, y, type); break; }
        case DWT_Sanghavi: {
            WImageRecovered = WatermarkEmbedding::DecodeBitmapSanghavi(JPGData, WImageDecoding, x, y, type); break; }
        case DWT_Soheili: {
            WImageRecovered = WatermarkEmbedding::DecodeBitmapSoheili(JPGData, WImageEncoding, x, y, Q, type); break;}
        default: break;
     }
     */

    if (JPGData != nullptr)         delete[] JPGData;
    if (WImageEncoding != nullptr)  delete WImageEncoding;
    return 1;
}

